class SemanticCache:
    def __init__(self, threshold=0.9):
        self.encoder = SentenceTransformer(''all-MiniLM-L6-v2'')
        self.cache = []  # [(embedding, result), ...]
        self.threshold = threshold
        
    def get(self, prompt):
        prompt_embedding = self.encoder.encode(prompt)
        
        for cached_embedding, cached_result in self.cache:
            similarity = cosine_similarity(prompt_embedding, cached_embedding)
            
            if similarity > self.threshold:
                print(f" Cache hit! (similarit: {similarity:.2f})")
                return cached_result
        
        return None  # Pas trouv
    
    def set(self, prompt, result):
        embedding = self.encoder.encode(prompt)
        self.cache.append((embedding, result))

# Utilisation
